pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract TokenFaucet is ReentrancyGuard, Ownable {
    IERC20 public token;
    uint256 public constant FAUCET_AMOUNT = 1e18; // 1 token (assuming 18 decimals)
    uint256 public constant FAUCET_COOLDOWN = 24 hours;
    
    mapping(address => uint256) public lastClaimTime;

    constructor(address _tokenAddress) Ownable(msg.sender) {
        token = IERC20(_tokenAddress);
    }

    function requestTokens() external nonReentrant {
        require(canClaimTokens(msg.sender), "You need to wait before claiming again");
        require(token.balanceOf(address(this)) >= FAUCET_AMOUNT, "Faucet is empty");

        lastClaimTime[msg.sender] = block.timestamp;
        require(token.transfer(msg.sender, FAUCET_AMOUNT), "Token transfer failed");
    }

    function canClaimTokens(address _user) public view returns (bool) {
        return (block.timestamp >= lastClaimTime[_user] + FAUCET_COOLDOWN);
    }

    // Function to allow the owner to withdraw any excess tokens
    function withdrawTokens(uint256 _amount) external onlyOwner {
        require(token.transfer(msg.sender, _amount), "Token transfer failed");
    }

    // Function to check the contract's token balance
    function getContractBalance() external view returns (uint256) {
        return token.balanceOf(address(this));
    }
}","keccak256":"0x11f4fbed0ba67d0af725217bb0c68f3bbe9667b59e6e65bf2ac705b38e222054","license":"MIT"}},"version":1}
